import { useState } from 'react';
import userEvent from '@testing-library/user-event';

function App() {
  const[circulo, setCirculo] = useState([]);
  const[deleta,setDeleta] = useState([]);

  const MarcaCiruclo = (e) => {
    const novocirculo = {
      clientX: e.clientX,
      clientY: e.clientY
    };
    setCirculo((antigo) => [...antigo,novocirculo]) 
  };

  const deletaCirculo = () =>{
    const ultimodot = setCirculo;
    console.log(ultimodot);

  }

  return (

    <div className='page' onClick={MarcaCiruclo}>
      {circulo.map((item) =>  (
      <div className='dot' style={{left: item.clientX, top: item.clientY}} />
      ))}
      <button className='desfazer-btn' onClick={deletaCirculo}>Desfazer</button>
      {/* <button className='refazer-btn' onClick={refazCiruclo}>Resfazer</button> */}
    </div>
  );
}

export default App;
