import './App.css';
import { useState } from 'react';

function App() {
  const[circulo, setCirculo] = useState([])

  const MarcaCiruclo = (e) => {
    const novocirculo = {
      clientX: e.clientX,
      clientY: e.clientY
    }
    setCirculo((antigo) => [...antigo,novocirculo])
  };


  return (

    <div className='page' onClick={MarcaCiruclo}>
      {JSON.stringify(circulo)}
      {/* {circulo.map((item) =>  (
      <div className='circulo' style={{left: circulo.clientX, top: circulo.clientY}} />
      ))} */}
    </div>
  );
}

export default App;
