import './App.css';
import { useState } from 'react';

function App() {
  const[circulo, setCirculo] = useState([]);

  const MarcaCiruclo = (e) => {
    const novocirculo = {
      clientX: e.clientX,
      clientY: e.clientY
    };
    console.log(novocirculo);
    setCirculo((antigo) => [...antigo,novocirculo]) 
  };


  return (

    <div className='page' onClick={MarcaCiruclo}>
      {circulo.map((item) =>  (
      <span className='dot' style={{left: item.clientX, top: item.clientY}} />
      ))}
    </div>
  );
}

export default App;
